<?php

namespace App\Services;

use App\Mail\ReportMail;
use App\Models\Code_to_validate;
use App\Models\Gymkhana;
use App\Models\Participation;
use App\Models\Ticket;
use Illuminate\Support\Facades\Mail;
use Illuminate\Support\Str;

class GymkhanaService
{
    public static function createGymkhana($gymkhana_pic_Base64, $name, $password, $description, $starting_point, $state, $availability){
        //JUST TO SHOW WITHOUT API CONSUMER
        $gymkhana_pic_Base64="";
        $gymkhana_pic=Base64_decode($gymkhana_pic_Base64);
        $gymkhana=Gymkhana::create([
            'name' => $name,
            'password' => $password,
            'description' => $description,
            'starting_point' => $starting_point,
            'gymkhana_pic' => $gymkhana_pic,
            'state' => $state,
            'availability' => $availability,
        ]);
        return $gymkhana->id;
    }

    public static function updateGymkhana($id, $gymkhana_pic_Base64, $name, $password, $description, $starting_point, $state, $availability){
        //JUST TO SHOW WITHOUT API CONSUMER
        $gymkhana_pic_Base64="";
        $gymkhana_pic=Base64_decode($gymkhana_pic_Base64);
        $gymkhana = Gymkhana::findOrFail($id);
        $gymkhana->update([
            'name' => $name ?? $gymkhana->name,
            'password' => $password ?? $gymkhana->password,
            'description' => $description ?? $gymkhana->description,
            'starting_point' => $starting_point ?? $gymkhana->starting_point,
            'gymkhana_pic' => $gymkhana_pic ?? $gymkhana->gymkhana_pic,
            'state' => $state ?? $gymkhana->state,
            'availability' => $availability ?? $gymkhana->availability,
        ]);
        return true;
    }

    public static function showGymkhanasInJoiningRange($userId, $userLatitude, $userLongitude)
    {
        $maxDistance = 10;

        $userAlreadyInGymkhanasIds = Participation::where('id_user', $userId)
            ->where('privilege', '!=', 6)
            ->whereHas('gymkhana', function ($query) {
                $query->where('availability', 1)
                    ->where('state', '!=', 3);
            })->pluck('id_gymkhana');

        return Gymkhana::whereNotIn('id', $userAlreadyInGymkhanasIds)
            ->where('availability', 1)
            ->whereIn('state', [1, 2])
            ->withinDistanceOf($userLatitude, $userLongitude, $maxDistance)
            ->get();
    }

    public static function getGymkhanaByAvailability($availability)
    {
        return Gymkhana::where('availability', $availability)->get();
    }

    public static function getGymkhanaByState($state)
    {
        return Gymkhana::where('state', $state)->get();
    }

    public static function updateGymkhanaAvailability($id, $availability)
    {
        Gymkhana::findOrFail($id)->update(['availability'=>$availability]);
        return true;
    }
    public static function setAvailabilityToPublic ($id)
    {
        Gymkhana::findOrFail($id)->update(['availability' => 1]);
        return true;
    }
    public static function setAvailabilityToPrivate($id)
    {
        Gymkhana::findOrFail($id)->update(['availability' => 0]);
        return true;
    }

    public static function updateGymkhanaState($id, $state)
    {
        Gymkhana::findOrFail($id)->update(['state'=>$state]);
        return true;
    }
    public static function setStateToPublished($id)
    {
        Gymkhana::findOrFail($id)->update(['state' => 1]);
        return true;
    }
    public static function setStateToOngoing($id)
    {
        Gymkhana::findOrFail($id)->update(['state' => 2]);
        return true;
    }
    public static function setStateToClosed($id)
    {
        Gymkhana::findOrFail($id)->update(['state' => 3]);
        return true;
    }

    public static function deleteGymkhana($id)
    {
        Participation::where('id_gymkhana', $id)->delete();
        Code_to_validate::where('id_gymkhana', $id)->delete();
        Ticket::where('id_content', $id)->where('type', 2)->delete();
        Gymkhana::findOrFail($id)->delete();
        return true;
    }

    public static function report($id, $userId){
        $gymkhana = Gymkhana::findOrFail($id);
        $img_base_64=base64_encode($gymkhana->gymkhana_pic);
        $token = Str::random(60);
        if($gymkhana->reported != 1){
            Ticket::create([
                'id_content' => $gymkhana->id,
                'type' => 2,
                'token' => $token
            ]);
            $gymkhana->update(['reported' => 1]);
            Mail::to('ticketsgymkhanamanager@gmail.com')->send(new ReportMail($gymkhana, $img_base_64, $userId, 2, $token));
        }
    }

    public static function closeReportTicket($id){
        $code = Gymkhana::findOrFail($id);
        if($code->reported != 0){
            $code->update(['reported' => 0]);
        }
    }

    public static function resetPicture($id){
        $defaultPic=base64_decode("");
        Gymkhana::where('id', $id)->update([
            'gymkhana_pic' => base64_decode($defaultPic)
        ]);
    }

    public static function resetName($id){
        Gymkhana::where('id', $id)->update([
            'name' => 'RemovedName'
        ]);
    }

    public static function resetDescription($id){
        Gymkhana::where('id', $id)->update([
            'description' => 'RemovedDescription'
        ]);
    }

    public static function resetPassword($id){
        Gymkhana::where('id', $id)->update([
            'password' => null
        ]);
    }

}
